import React, { useState } from "react";
import Love from './Love.png';
import Sad from './Sad.png';
import Like from './Like.png';

function TextEmojiDisplay() {
  const [text, setText] = useState("");
  const [emoji, setEmoji] = useState(null);

  const handleChange = (event) => {
    const input = event.target.value.toLowerCase();
    setText(input);
    if (input === "happy") setEmoji(Love);
    else if (input === "sad") setEmoji(Sad);
    else if (input === "like") setEmoji(Like);
    else setEmoji(null);
  };

  return (
    <div className="App">
      <input type="text" value={text} onChange={handleChange} placeholder="Type 'happy', 'sad' or 'like'" />
      <label>
        {emoji && <img src={emoji} alt={text} />}
      </label>
    </div>
  );
}

export default TextEmojiDisplay;
